import React from 'react';

// https://westzero.tistory.com/112
String.prototype.toKorChars = function () {
  let cCho = [
      'ㄱ',
      'ㄲ',
      'ㄴ',
      'ㄷ',
      'ㄸ',
      'ㄹ',
      'ㅁ',
      'ㅂ',
      'ㅃ',
      'ㅅ',
      'ㅆ',
      'ㅇ',
      'ㅈ',
      'ㅉ',
      'ㅊ',
      'ㅋ',
      'ㅌ',
      'ㅍ',
      'ㅎ',
    ],
    cJung = [
      'ㅏ',
      'ㅐ',
      'ㅑ',
      'ㅒ',
      'ㅓ',
      'ㅔ',
      'ㅕ',
      'ㅖ',
      'ㅗ',
      'ㅘ',
      'ㅙ',
      'ㅚ',
      'ㅛ',
      'ㅜ',
      'ㅝ',
      'ㅞ',
      'ㅟ',
      'ㅠ',
      'ㅡ',
      'ㅢ',
      'ㅣ',
    ],
    cJong = [
      '',
      'ㄱ',
      'ㄲ',
      'ㄳ',
      'ㄴ',
      'ㄵ',
      'ㄶ',
      'ㄷ',
      'ㄹ',
      'ㄺ',
      'ㄻ',
      'ㄼ',
      'ㄽ',
      'ㄾ',
      'ㄿ',
      'ㅀ',
      'ㅁ',
      'ㅂ',
      'ㅄ',
      'ㅅ',
      'ㅆ',
      'ㅇ',
      'ㅈ',
      'ㅊ',
      'ㅋ',
      'ㅌ',
      'ㅍ',
      'ㅎ',
    ],
    cho,
    jung,
    jong;
  let str = this,
    cnt = str.length,
    chars = [],
    cCode;
  for (let i = 0; i < cnt; i++) {
    cCode = str.charCodeAt(i);
    if (cCode == 32) {
      chars.push(' ');
      continue;
    } // 한글이 아닌 경우
    if (cCode < 0xac00 || cCode > 0xd7a3) {
      chars.push(str.charAt(i));
      continue;
    }
    cCode = str.charCodeAt(i) - 0xac00;

    jong = cCode % 28;
    // 종성
    jung = ((cCode - jong) / 28) % 21;

    // 중성
    cho = ((cCode - jong) / 28 - jung) / 21;
    // 초성

    //기본 코드 테스트가 ㅌㅔㅅ-ㅌ- 형식으로 저장됨
    // chars.push(cCho[cho], cJung[jung]);
    // if (cJong[jong] !== '') {
    //     chars.push(cJong[jong]);
    //     }

    //  테스트라는 문장이 있으면 ㅌ테ㅅ스ㅌ트 형식으로 저장되도록함 (타이핑을 위해서)
    chars.push(cCho[cho]);
    chars.push(String.fromCharCode(44032 + cho * 588 + jung * 28));
    if (cJong[jong] !== '') {
      chars.push(String.fromCharCode(44032 + cho * 588 + jung * 28 + jong));
    }
  }

  return chars;
};

//타이핑할 문장
var typing1 = '안녕하세요';
var typing2 = '매일매일 SKILL UP 진행 중인';
var typing3 = '웹 퍼블리셔 김해림 입니다.';
var typeing1 = [],
  typeing2 = [],
  typeing3 = [];
typing1 = typing1.split(''); // 한글자씩자름
typing2 = typing2.split(''); // 한글자씩자름
typing3 = typing3.split(''); // 한글자씩자름

//각글자 초성,중성,종성으로 나눔
for (var i = 0; i < typing1.length; i++) {
  typeing1[i] = typing1[i].toKorChars();
}
for (var i = 0; i < typing2.length; i++) {
  typeing2[i] = typing2[i].toKorChars();
}
for (var i = 0; i < typing3.length; i++) {
  typeing3[i] = typing3[i].toKorChars();
}

//출력할 엘리먼트요소 가져옴
var resultDiv1 = document.getElementsByClassName('typing1')[0];
var resultDiv2 = document.getElementsByClassName('typing2')[0];
var resultDiv3 = document.getElementsByClassName('typing3')[0];

//
var text = '';
var i = 0;
var j = 0;
var v = 0;

//총글자수
var imax1 = typeing1.length;
var imax2 = typeing2.length;
var imax3 = typeing3.length;

//setInterval을 이용해 반복적으로 출력
var inter = setInterval(typi, 150);
var inter2;
var inter3;

function typi() {
  //글자수만큼 반복후 종료
  resultDiv1.classList.add('cursor');
  if (i <= imax1 - 1) {
    //각 글자가 초성 중성 종성 순서대로 추가되도록
    var jmax1 = typeing1[i].length;
    resultDiv1.innerHTML = text + typeing1[i][j];
    j++;
    if (j == jmax1) {
      text += typeing1[i][j - 1]; //초성중성종성 순서대로 출력된 글자는 저장 ( 다음 글자와 이어붙이기 위해서 )
      i++;
      j = 0;
    }
  } else if (i <= imax2 - 1) {
    clearInterval(inter);
    text = '';
    i = 0;
    setTimeout(function () {
      resultDiv1.classList.remove('cursor');
      setTimeout(function () {
        resultDiv2.classList.add('cursor');
        setTimeout(function () {
          inter2 = setInterval(typi2, 150);
        }, 400);
      }, 300);
    }, 400);
  }
}

function typi2() {
  //글자수만큼 반복후 종료

  if (i <= imax2 - 1) {
    //각 글자가 초성 중성 종성 순서대로 추가되도록
    var jmax2 = typeing2[i].length;
    resultDiv2.innerHTML = text + typeing2[i][j];
    j++;
    if (j == jmax2) {
      text += typeing2[i][j - 1]; //초성중성종성 순서대로 출력된 글자는 저장 ( 다음 글자와 이어붙이기 위해서 )
      i++;
      j = 0;
    }
  } else if (j <= imax3 - 1) {
    clearInterval(inter);
    text = '';
    j = 0;
    setTimeout(() => {
      resultDiv2.classList.remove('cursor');
      setTimeout(() => {
        resultDiv3.classList.add('cursor');
        setTimeout(() => {
          inter3 = setInterval(typi3, 150);
        }, 400);
      }, 300);
    }, 400);
  }
}
function typi3() {
  if (j <= imax3 - 1) {
    var jmax3 = typeing3[j].length;
    resultDiv3.innerHTML = text + typeing3[j][v];
    v++;
    if (v == jmax3) {
      text += typeing3[j][v - 1]; //초성중성종성 순서대로 출력된 글자는 저장 ( 다음 글자와 이어붙이기 위해서 )
      j++;
      v = 0;
    }
  } else {
    clearInterval(inter);
  }
}

const Typing = () => {
  return (
    <div>
      <section id='main' className='section'>
        <header id='fancy' className='fancy'>
          <p>
            <span className='typing1'></span>
          </p>
          <p>
            <span className='typing2'></span>
          </p>
          <p>
            <span className='typing3'></span>
          </p>
        </header>
      </section>
    </div>
  );
};

export default Typing;
